import React, { useState } from 'react';
import './App.css';

function App() {
  const [desktopState, setDesktopState] = useState('Homedesktop1');

  const handleButtonClick = (newState) => {
    setDesktopState(newState);
  };

  return (
    <div className="Homepage" style={{ width: '100vw', height: '100vh', position: 'relative', background: 'white', border: 'hidden red', overflow: 'hidden' }}>
      {desktopState === 'Homedesktop1' && (
        <div className="Homedesktop1">
          {/* Content for Homedesktop1 */}
          <button className="ButtonServices" onClick={() => handleButtonClick('Homedesktop2')}>
            Services
          </button>
        </div>
      )}
      {desktopState === 'Homedesktop2' && (
        <div className="Homedesktop2">
          {/* Content for Homedesktop2 */}
          <button className="ButtonAbout" onClick={() => handleButtonClick('Homedesktop1')}>
            About Us
          </button>
          <button className="ButtonServices" onClick={() => handleButtonClick('Homedesktop3')}>
            Services
          </button>
        </div>
      )}
      {desktopState === 'Homedesktop3' && (
        <div className="Homedesktop3">
          {/* Content for Homedesktop3 */}
          <button className="ButtonAbout" onClick={() => handleButtonClick('Homedesktop1')}>
            About Us
          </button>
        </div>
      )}

      {/* Footer and Nav elements */}
    </div>
  );
}

export default App;
