import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  // State to keep track of the selected button
  const [selectedButton, setSelectedButton] = useState('aboutUs');

  return (
    <div className="Homepage" style={{width: '100vw', height: '100vh', position: 'relative', background: 'white', border:'hidden red', overflow:'hidden'}}>
        {/* Homepage 1 */}
        <div className={selectedButton === 'aboutUs' ? 'Homedesktop1' : 'hidden'} style={{left: 0, top: 0, position: 'absolute', border: '1px black solid'}}>
            {/* Your Homepage 1 content here */}
        </div>

        {/* Homepage 2 */}
        <div className={selectedButton === 'services' ? 'Homedesktop2' : 'hidden'} style={{left: 0, top: 0, position: 'absolute', border: '1px black solid'}}>
            {/* Your Homepage 2 content here */}
        </div>

        {/* Homepage 3 */}
        <div className={selectedButton === 'blog' ? 'Homedesktop3' : 'hidden'} style={{left: 0, top: 0, position: 'absolute', border: '1px black solid'}}>
            {/* Your Homepage 3 content here */}
        </div>

        {/* Buttons */}
        <div className="Navmenudesktop" style={{width: 828, height: '7vh', right: '0vw', position: 'absolute', justifyContent: 'center', alignItems: 'flex-start', display: 'inline-flex'}}>
            <div className="ListitemDesktopNa" onClick={() => setSelectedButton('aboutUs')} style={{width: 207, height: '100%', paddingLeft: 38, paddingRight: 38, paddingTop: 0, paddingBottom: 0, background: '#0E0E0E', justifyContent: 'center', alignItems: 'center', display: 'inline-flex'}}>
                <div className="Label" style={{width: 131, textAlign: 'center', color: 'white', fontSize: 20, fontFamily: 'Roboto', fontWeight: '500', lineHeight: 24, letterSpacing: 2, wordWrap: 'break-word'}}>About Us</div>
            </div>
            <div className="ListitemDesktopNa" onClick={() => setSelectedButton('services')} style={{width: 207, height: '100%', paddingLeft: 38, paddingRight: 38, paddingTop: 0, paddingBottom: 0, background: '#0E0E0E', justifyContent: 'center', alignItems: 'center', display: 'inline-flex'}}>
                <div className="Label" style={{width: 131, textAlign: 'center', color: 'white', fontSize: 20, fontFamily: 'Roboto', fontWeight: '500', lineHeight: 24, letterSpacing: 2, wordWrap: 'break-word'}}>Services</div>
            </div>
            <div className="ListitemDesktopNa" onClick={() => setSelectedButton('blog')} style={{width: 207, height: '100%', paddingLeft: 38, paddingRight: 38, paddingTop: 0, paddingBottom: 0, background: '#0E0E0E', justifyContent: 'center', alignItems: 'center', display: 'inline-flex'}}>
                <div className="Label" style={{width: 131, textAlign: 'center', color: 'white', fontSize: 20, fontFamily: 'Roboto', fontWeight: '500', lineHeight: 24, letterSpacing: 2, wordWrap: 'break-word'}}>Blog</div>
            </div>
        </div>
    </div>
  );
}

export default App;
